mapper.update:
UPDATE TB_MNGR SET ID = #{id}, NAME = #{name},
PASSWORD = #{password}, PASSWORD_KEY = #{passwordKey},
UPT_DATE = NOW() WHERE MNGR_SEQ = #{mngrSeq}

mapper.select:
SELECT T.RNUM, T.RESERVATION_SEQ, T.RESERVATION_DATE, T.RESERVATION_NAME, T.PHONE, T.CHOOSE_AREA, T.RESERVATION_CONTENT, T.USE_YN, T.REG_DATE, T.UPT_DATE FROM (SELECT @rownum:=@rownum+1 RNUM, R.RESERVATION_SEQ, DATE_FORMAT(R.RESERVATION_DATE,'%Y-%m-%d %H:%i') RESERVATION_DATE, R.RESERVATION_NAME, R.PHONE, R.CHOOSE_AREA, R.RESERVATION_CONTENT, R.USE_YN, DATE_FORMAT(R.REG_DATE,'%Y-%m-%d %H:%i') REG_DATE, DATE_FORMAT(R.UPT_DATE,'%Y-%m-%d %H:%i') UPT_DATE FROM TB_RESERVATION R INNER JOIN (SELECT @ROWNUM := 0) C
R.PHONE LIKE CONCAT('%',#{searchWord},'%') OR R.RESERVATION_NAME LIKE CONCAT('%',#{searchWord},'%')
ORDER BY R.RESERVATION_DATE DESC) T LIMIT #{startPostNumber} , #{blockPostCnt}

mapper.update:
UPDATE TB_NOTICE SET
	MAIN_VIEW_YN = #{mainViewYn}
WHERE
	NOTICE_SEQ = #{noticeSeq}
